## server.R ##

  shinyServer(
    function(input, output) { 
      
      ## REACTIVE SUBSETS ## 
      # Generated by reactive expressions to cache selected values 
      
      sisInput <- reactive({  
        scrub_sis %<>% 
          filter(as.Date(sis_date) >= input$dateRange[1]
                 & as.Date(sis_date) <= input$dateRange[2])
      })
      
      tos1Input <- reactive({
        s1_tos <-
          sisInput() %>%
          filter(is.na(fake_id) == FALSE) %>% # Remove empty randomized IDs
          group_by(fake_id) %>% 
          filter(as.Date(sis_date) == max(as.Date(sis_date))) %>% # Most recent per ID
          ungroup() %>% droplevels() %>%
          select(fake_id,agency,ends_with("tos")) %>% 
          select(fake_id,agency,starts_with("s1")) %>%
          mutate_each(funs(as.character), -fake_id, -agency) %>%
          gather(field, tos, s1a_1_tos:s1f_8_tos) %>%
          rename(type = tos 
          ) %>%
          mutate(type_n = as.numeric(type),
                 field = gsub("tos","",field),
                 id = as.factor(paste0(fake_id,field))
          ) %>%
          group_by(id, agency, field, type) %>%
          summarize(n = n_distinct(id),
                    type_n = sum(type_n)) %>%
          ungroup() 
        
        s1_fqy <-
          sisInput() %>%
          filter(is.na(fake_id) == FALSE) %>% # Remove empty Medicaid IDs
          group_by(fake_id) %>% 
          filter(as.Date(sis_date) == max(as.Date(sis_date))) %>% # Most recent per ID
          ungroup() %>% droplevels() %>%
          select(fake_id,agency,ends_with("fqy")) %>% 
          select(fake_id, agency, starts_with("s1")) %>%
          mutate_each(funs(as.character), -fake_id, -agency) %>%
          gather(field, fqy, s1a_1_fqy:s1f_8_fqy) %>%
          rename(frequency = fqy) %>%
          mutate(frequency_n = as.numeric(frequency),
                 field = gsub("fqy","",field),
                 id = as.factor(paste0(fake_id,field))
          ) %>%
          group_by(id,agency, field, frequency) %>%
          summarize(frequency_n = sum(frequency_n)) %>%
          ungroup() 
        
        s1_dst <-
          sisInput() %>%
          filter(is.na(fake_id) == FALSE) %>% # Remove empty Medicaid IDs
          group_by(fake_id) %>% 
          filter(as.Date(sis_date) == max(as.Date(sis_date))) %>% # Most recent per ID
