          s2 %>%
          select(id,agency,field,type,type_n,frequency,frequency_n,DST,DST_n) %>%
          mutate(field = car::recode(field,
                                     "'s2_1_' = 'Self-advocacy';
                                     's2_2_' = 'Money management';
                                     's2_3_' = 'Exploited by others';
                                     's2_4_' = 'Legal responsibility';
                                     's2_5_' = 'Participation';
                                     's2_6_' = 'Legal services';
                                     's2_7_' = 'Decision making';
                                     's2_8_' = 'Other advocacy'"),
                 type = car::recode(type,
                                    "'0' = 'None';
                                    '1' = 'Monitoring';
                                    '2' = 'Coaching';
                                    '3' = 'Partial Physical Assistance';
                                    '4' = 'Full Physical Support'"),
                 frequency = car::recode(frequency,
                                         "'0' = 'Minimal';
                                         '1' = 'Monthly';
                                         '2' = 'Weekly';
                                         '3' = 'Daily';
                                         '4' = 'Hourly'"),
                 DST = car::recode(DST,
                                   "'0' = 'None';
                                   '1' = 'Under 30 min';
                                   '2' = 'Under 2 hrs';
                                   '3' = '2-4 hrs';
                                   '4' = 'Over 4 hrs'")) 
        
        # Summarize for app
        
        tos_section2 <-
          s2 %>%
          group_by(agency, field, type, frequency, DST) %>%
          summarize(n = n(),
                    type_tot = sum(type_n),
                    frequency_tot = sum(frequency_n),
                    DST_tot = sum(DST_n)) %>%
          ungroup()
        
      })
      
