                                    's1f_6_' = 'Appropriate social skills';
                                    's1f_7_' = 'Intimate relationships';
                                    's1f_8_' = 'Volunteer work';"))
        
      })
      
      sec2Input <- reactive({
        
        sec2Input <-
          sisInput() %>%
          filter(is.na(fake_id) == FALSE) %>% # Remove empty Medicaid IDs
          group_by(fake_id) %>% 
          filter(as.Date(sis_date) == max(as.Date(sis_date))) %>% # Most recent per ID
          ungroup() %>% droplevels() %>%
          select(fake_id,agency,s2_1_fqy:s2_8_for) %>%
          select(fake_id, agency, contains("tos"), contains("fqy"), contains("dst")) %>%
          gather(field, score, s2_1_tos:s2_8_dst) %>%
          mutate(item = gsub("tos|fqy|dst", "", field)) %>%
          group_by(fake_id, agency, item) %>%
          summarize(score = sum(score)) %>%
          mutate(item = car::recode(item,
                                    "'s2_1_' = 'Self-advocacy';
                                     's2_2_' = 'Money management';
                                     's2_3_' = 'Exploited by others';
                                     's2_4_' = 'Legal responsibility';
                                     's2_5_' = 'Participation';
                                     's2_6_' = 'Legal services';
                                     's2_7_' = 'Decision making';
                                     's2_8_' = 'Other advocacy'"))
        
      })

      sec3Input <- reactive({
        
        sisInput() %>%
          select(fake_id, agency, LivingType, starts_with("s3")) %>%
          select(fake_id, agency, LivingType, ends_with("support")) %>%
          gather(item,score,s3a_1_support:s3b_13_support) %>%
          mutate(subtype = car::recode(item,
